UPDATE `locales_quest` SET `offerrewardtext_loc1`='My honor is restored.$B$BPartially.$B$BMy tale continues, $C, for I have left portions of my honor elsewhere in this forest.$B$BIt has not been a very easy journey for me.' WHERE `entry`=28829;
UPDATE `locales_quest` SET `offerrewardtext_loc1`='Be glad that the adult giants are trapped on the other side of that gorge. They are far hardier than these tumblers you fight.$B$BI feel rejuvenated by your courage, $C. Perhaps all hope is not lost after all...' WHERE `entry`=28830;
UPDATE `locales_quest` SET `offerrewardtext_loc1`='If I survive my wounds, I will always remember your name, $C.$B$BWhenever the wind whistles through the trees, I will always hear your name.$B$BThank you.' WHERE `entry`=28831;
