UPDATE `db_script_string` SET `content_loc8`='Да, они воплощают порок, но я бессилен их остановить. Ты предупредишь народ Тунка\'ло, $Gстранник:странница;, что их прошлое и их предки в опасности?' WHERE `entry`=2000000613;
