UPDATE `locales_quest` SET `offerrewardtext_loc6`='Honor the Hillsbrad Foothills flame.' WHERE `entry`=11853;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Honor the Nagrand flame.' WHERE `entry`=11854;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Honor the Stonetalon Mountains flame.' WHERE `entry`=11856;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Honor the Swamp of Sorrows flame.' WHERE `entry`=11857;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Honor the Terokkar Forest flame.' WHERE `entry`=11858;
