UPDATE `locales_quest` SET `offerrewardtext_loc6`='King Stormheart sends his most sincere thanks for reuniting him with his brothers. It seems he\'s off to settle a score now - perhaps you\'ll meet with him again on Arthas\'s doorstep.' WHERE `entry`=12973;
UPDATE `locales_quest` SET `RequestItemsText_loc6`='My pardons, $Glad:lass;! I got all caught up in tha moment and I neglected ye entirely.' WHERE `entry`=12973;
