UPDATE `locales_quest` SET `offerrewardtext_loc6`='So, $Gbrother:sister;, you think that completing a few simple chores makes you a heroine?$B$BYou shall learn....' WHERE `entry`=13063;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='You now know a bit of the history of this land... my land.$B$BI call upon you to assist me in setting these wrongs to right.' WHERE `entry`=13064;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='It is good to know the people of this land still pay homage to the olden races. I bid you well, $N, and offer you this token...' WHERE `entry`=13065;
