UPDATE `locales_quest` SET `offerrewardtext_loc6`='Your bravery on the field has shown the crusaders that we are fearless in the face of the Lich King\'s minions.' WHERE `entry`=13857;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Your bravery on the field has shown the crusaders that we are steadfast allies in the battle against the Lich King.' WHERE `entry`=13858;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Your bravery on the field has shown the crusaders that we are ready to face any enemy.' WHERE `entry`=13859;
