UPDATE `locales_quest` SET `offerrewardtext_loc6`='I have witnessed your actions here, $R. Your presence here is both welcome and timely.' WHERE `entry`=14292;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Well done, $N. We\'ll make sure Krennan makes it out of the city alive.' WHERE `entry`=14293;
