UPDATE `locales_quest` SET `offerrewardtext_loc6`='Excellent news, $C! If our tactical deployments continue to be this successful, the Horde will be in full retreat before long.' WHERE `entry`=24220;
UPDATE `locales_quest` SET `RequestItemsText_loc6`='Tell me, $N. What news do you bring from the front?' WHERE `entry`=24220;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='You\'ve brought great honor to the Horde, $N. Your victory today will ensure a thousand victories tomorrow!' WHERE `entry`=24221;
