UPDATE `locales_quest` SET `offerrewardtext_loc6`='Fine work, $N. It looks as though our work here is nearly complete.' WHERE `entry`=27624;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Such aberrations in the face of the light... You are brave to face them, $N.' WHERE `entry`=27625;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Thank the Light. We will not let the Forsaken have anything that can be turned against us, no matter how small.' WHERE `entry`=27626;
