UPDATE `locales_quest` SET `offerrewardtext_loc6`='She may now ascend to the great horsey heaven in the stars. We will never forget you, Twinkles. NEVER!' WHERE `entry`=28231;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='These rations will really help the troops out $N. Thank you for your help.' WHERE `entry`=28232;
