UPDATE `locales_quest` SET `objectives_loc6`='Speak with Quartermaster Kaoshin at the Black Tooth Hovel in Burning Steppes.',`offerrewardtext_loc6`='Where have you been, grunt?  You\'re late!  Give me one reason why I shouldn\'t kill you on the spot!' WHERE `entry`=28432;
