UPDATE `locales_quest` SET `offerrewardtext_loc6`='You cleared that area out?  Looks like you might be more useful to us that we thought, $R.' WHERE `entry`=28668;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Ahh, the wardens key\'s. Another job well done, $N. These will come in very handy.' WHERE `entry`=28669;
