UPDATE `locales_quest` SET `offerrewardtext_loc6`='You did it!  Maybe we can finally get some ships into port now. Thank you $N.' WHERE `entry`=28678;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Nice work on the undead up there $N. We really appreciate the help.' WHERE `entry`=28679;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='This rum will really help us out, $N. They might not feel great in the morning but at least they will go to sleep happy.' WHERE `entry`=28680;
