UPDATE `locales_quest` SET `offerrewardtext_loc6`='You killed him?  We have been trying to take that guy out for weeks!  We thank you, $N.' WHERE `entry`=28682;
UPDATE `locales_quest` SET `offerrewardtext_loc6`='Nice work on those spiders $N. At this rate, we will be taking that forest in no time.' WHERE `entry`=28683;
